mapper.select:
SELECT R.CAR_RESVE_NO , R.CAR_NO , C.CAR_NM , R.EMP_NO , R.RESVE_BEGIN_DATE , R.RESVE_END_DATE , R.RESVE_CN , R.RTURN_CD , R.ALL_DAY_CD
FROM CAR_RESERVATION R INNER JOIN CAR C ON R.CAR_NO = C.CAR_NO WHERE 1 = 1
AND R.CAR_NO = #{carNo}

mapper.update:
		UPDATE CAR_RESERVATION SET 
   							  CAR_NO = #{carNo}
							, EMP_NO = #{empNo}
							, RESVE_BEGIN_DATE = TO_DATE(#{resveBeginDate},'YYYY-MM-DD HH24:MI:SS')
							, RESVE_END_DATE = TO_DATE(#{resveEndDate},'YYYY-MM-DD HH24:MI:SS')
							, RESVE_CN = #{resveCn}
							, RTURN_CD = #{rturnCd}
							, ALL_DAY_CD = #{allDayCd}
		WHERE 
							CAR_RESVE_NO = #{carResveNo}

UPDATE 
		CAR_RESERVATION 
SET 
		RTURN_CD = #{rturnCd}
WHERE 
		CAR_RESVE_NO = #{carResveNo}
