mapper.insert:
	INSERT INTO NOTICE_BOARD
		VALUES(
		(SELECT NVL(MAX(TO_NUMBER(NOTICE_BRD_NO)), 0)+1 FROM NOTICE_BOARD)
		,#{empNo}
		,#{noticeBrdSj}
		,SYSDATE
		,0
		,''
        ,#{noticeBrdSeCd}
        ,TO_CHAR(TO_DATE(#{fixingEndDate}, 'YYYY-MM-DD'), 'YYYYMMDD')
		,#{noticeBrdCn}
		,(SELECT MAX(TO_NUMBER(ATCHMNFL_NO)) FROM ATTACHED_FILE)
		)

	INSERT INTO NOTICE_BOARD
		VALUES(
		(SELECT NVL(MAX(TO_NUMBER(NOTICE_BRD_NO)), 0)+1 FROM NOTICE_BOARD)
