mapper.update:
UPDATE NOTICE_BOARD
SET 
    NOTICE_BRD_SJ =#{noticeBrdSj}
    ,UPDT_DATE =SYSDATE
    ,NOTICE_BRD_SE_CD =#{noticeBrdSeCd}
    ,FIXING_END_DATE = TO_CHAR(TO_DATE(#{fixingEndDate}, 'YYYY-MM-DD'),'YYYYMMDD')
    ,NOTICE_BRD_CN = #{noticeBrdCn}
    ,ATCHMNFL_NO = #{atchmnflNo}
WHERE
    NOTICE_BRD_NO =#{noticeBrdNo}

UPDATE NOTICE_BOARD
SET 
    NOTICE_BRD_SJ =#{noticeBrdSj}
    ,UPDT_DATE =SYSDATE
    ,NOTICE_BRD_SE_CD =#{noticeBrdSeCd}
    ,FIXING_END_DATE = TO_CHAR(TO_DATE(#{fixingEndDate}, 'YYYY-MM-DD'),'YYYYMMDD')
    ,NOTICE_BRD_CN = #{noticeBrdCn}
WHERE
    NOTICE_BRD_NO =#{noticeBrdNo}
